import { useState } from 'react';
import { 
  MantineProvider, 
  Container, 
  Title, 
  TextInput, 
  NumberInput, 
  Button, 
  Group, 
  Text, 
  Select 
} from '@mantine/core';

function AddMovie() {
  const [form, setForm] = useState({ 
    name: '', 
    length: 0, 
    genre: '', 
    ageLimit: 0 
  });
  const [message, setMessage] = useState('');
  const [errors, setErrors] = useState<Record<string, string>>({});

  const validateField = (name: string, value: any) => {
    const newErrors = { ...errors };
    
    switch (name) {
      case 'name':
        if (!value.trim()) newErrors[name] = 'Név kötelező';
        else if (value.length < 2) newErrors[name] = 'Legalább 2 karakter';
        else delete newErrors[name];
        break;
      case 'length':
        if (value < 1) newErrors[name] = 'Legalább 1 perc';
        else delete newErrors[name];
        break;
      case 'genre':
        if (!value) newErrors[name] = 'Műfaj kötelező';
        else delete newErrors[name];
        break;
      case 'ageLimit':
        if (value < 0) newErrors[name] = 'Nem lehet negatív';
        else if (value > 18) newErrors[name] = 'Max 18 év';
        else delete newErrors[name];
        break;
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    // Összes mező validálása
    const isValid = Object.entries(form).every(([key, value]) => 
      validateField(key, value)
    );
    
    if (!isValid) return;

    try {
      const response = await fetch('http://localhost:5214/api/Movie/add-movie', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: form.name,
          length: form.length,
          genre: form.genre,
          ageLimit: form.ageLimit
        }),
      });

      const data = await response.json();
      
      if (response.ok) {
        setMessage('Film sikeresen hozzáadva!');
        setForm({ name: '', length: 0, genre: '', ageLimit: 0 });
      } else {
        if (data.errors) {
          const backendErrors = Object.entries(data.errors).reduce((acc: any, [key, value]: [string, any]) => {
            acc[key.toLowerCase()] = value.join(' ');
            return acc;
          }, {});
          setErrors(backendErrors);
        }
        setMessage(data.title || 'Hiba történt a mentés során');
      }
    } catch (error) {
      setMessage('Szerverhiba. Próbáld újra később.');
    }
  };

  return (
    <MantineProvider>
      <Container>
        <Title order={2} mb="md">Új film hozzáadása</Title>
        <form onSubmit={handleSubmit}>
          <TextInput
            label="Film címe"
            value={form.name}
            onChange={(e) => {
              setForm({ ...form, name: e.target.value });
              validateField('name', e.target.value);
            }}
            error={errors.name}
            withAsterisk
            mb="sm"
          />

          <NumberInput
            label="Hossz (perc)"
            value={form.length}
            onChange={(value) => {
              setForm({ ...form, length: value as number });
              validateField('length', value);
            }}
            min={1}
            max={600}
            error={errors.length}
            withAsterisk
            mb="sm"
          />

          <Select
            label="Műfaj"
            data={['Akció', 'Dráma', 'Vígjáték', 'Horror', 'Sci-Fi', 'Romantikus']}
            value={form.genre}
            onChange={(value) => {
              setForm({ ...form, genre: value || '' });
              validateField('genre', value);
            }}
            error={errors.genre}
            withAsterisk
            mb="sm"
          />

          <NumberInput
            label="Korhatár"
            value={form.ageLimit}
            onChange={(value) => {
              setForm({ ...form, ageLimit: value as number });
              validateField('ageLimit', value);
            }}
            min={0}
            max={18}
            error={errors.ageLimit}
            withAsterisk
            mb="sm"
          />

          <Group mt="md">
            <Button 
              type="submit" 
              disabled={Object.keys(errors).length > 0}
            >
              Hozzáadás
            </Button>
          </Group>
        </form>

        {message && (
          <Text mt="md" color={message.includes('sikeresen') ? 'green' : 'red'}>
            {message}
          </Text>
        )}
      </Container>
    </MantineProvider>
  );
}

export default AddMovie;
