import { useEffect, useState } from 'react';
import { MantineProvider, Container, Title, Table, Text, Center } from '@mantine/core';

interface Movie {
  id: number;
  name: string;
  length: number;
  genre: string;
  ageLimit: number;
}

function App() {
  const [movies, setMovies] = useState<Movie[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  useEffect(() => {
    fetch('https://localhost:5214/api/Movie/List')
      .then((res) => {
        if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);
        return res.json();
      })
      .then((data) => setMovies(data))
      .catch((err) => {
        console.error('Error fetching movies:', err);
        setError(true);
      })
      .finally(() => setLoading(false));
  }, []);

  const renderContent = () => {
    if (loading) {
      return (
        <Center mt="md">
          <Text>Bet√∂lt√©s...</Text>
        </Center>
      );
