import { useState } from 'react';
import { MantineProvider, Container, Title, TextInput, Button, Group, Text } from '@mantine/core';

function UpdateScreening() {
  const [id, setId] = useState('');
  const [form, setForm] = useState({ dateTime: '', room: '' });
  const [message, setMessage] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await fetch(`https://localhost:5214/api/Screening/update-screening/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      if (response.ok) setMessage('Vetítés frissítve!');
      else setMessage('Vetítés nem található');
    } catch (error) {
      setMessage('Hiba történt');
    }
  };

  return (
    <MantineProvider>
      <Container>
        <Title order={2}>Vetítés frissítése</Title>
        <form onSubmit={handleSubmit}>
          <TextInput
            label="Vetítés ID"
            value={id}
            onChange={(e) => setId(e.target.value)}
            required
          />
          <TextInput
            label="Dátum és idő"
            type="datetime-local"
            value={form.dateTime}
            onChange={(e) => setForm({ ...form, dateTime: e.target.value })}
          />
          <TextInput
            label="Terem"
            value={form.room}
            onChange={(e) => setForm({ ...form, room: e.target.value })}
          />
          <Group mt="md">
            <Button type="submit">Frissítés</Button>
          </Group>
        </form>
        {message && <Text mt="md">{message}</Text>}
      </Container>
    </MantineProvider>
  );
}

export default UpdateScreening;
