import { useState } from 'react';
import { MantineProvider, Container, Title, TextInput, NumberInput, Button, Group, Text } from '@mantine/core';

function AddMovie() {
  const [form, setForm] = useState({ name: '', length: 0, genre: '', ageLimit: 0 });
  const [message, setMessage] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5214/api/Movie/add-movie', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      if (response.ok) {
        setMessage('Film hozzáadva!');
        setForm({ name: '', length: 0, genre: '', ageLimit: 0 });
      }
    } catch (error) {
      setMessage('Hiba történt');
    }
  };

  return (
    <MantineProvider>
      <Container>
        <Title order={2}>Új film</Title>
        <form onSubmit={handleSubmit}>
          <TextInput
            label="Név"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
            required
          />
          <NumberInput
            label="Hossz (perc)"
            value={form.length}
            onChange={(value) => setForm({ ...form, length: value as number })}
            required
          />
          <TextInput
            label="Műfaj"
            value={form.genre}
            onChange={(e) => setForm({ ...form, genre: e.target.value })}
            required
          />
          <NumberInput
            label="Korhatár"
            value={form.ageLimit}
            onChange={(value) => setForm({ ...form, ageLimit: value as number })}
            required
          />
          <Group mt="md">
            <Button type="submit">Hozzáadás</Button>
          </Group>
        </form>
        {message && <Text mt="md">{message}</Text>}
      </Container>
    </MantineProvider>
  );
}

export default AddMovie;
