import { useState } from 'react';
import { MantineProvider, Container, Title, TextInput, NumberInput, Button, Group, Text } from '@mantine/core';

function UpdateMovie() {
  const [id, setId] = useState('');
  const [form, setForm] = useState({ name: '', length: 0, genre: '', ageLimit: 0 });
  const [message, setMessage] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await fetch(`https://localhost:5214/api/Movie/update-movie/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      if (response.ok) setMessage('Film frissítve!');
      else setMessage('Film nem található');
    } catch (error) {
      setMessage('Hiba történt');
    }
  };

  return (
    <MantineProvider>
      <Container>
        <Title order={2}>Film frissítése</Title>
        <form onSubmit={handleSubmit}>
          <TextInput
            label="Film ID"
            value={id}
            onChange={(e) => setId(e.target.value)}
            required
          />
          <TextInput
            label="Név"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
          />
          <NumberInput
            label="Hossz (perc)"
            value={form.length}
            onChange={(value) => setForm({ ...form, length: value as number })}
          />
          <TextInput
            label="Műfaj"
            value={form.genre}
            onChange={(e) => setForm({ ...form, genre: e.target.value })}
          />
          <NumberInput
            label="Korhatár"
            value={form.ageLimit}
            onChange={(value) => setForm({ ...form, ageLimit: value as number })}
          />
          <Group mt="md">
            <Button type="submit">Frissítés</Button>
          </Group>
        </form>
        {message && <Text mt="md">{message}</Text>}
      </Container>
    </MantineProvider>
  );
}

export default UpdateMovie;
