import { useState } from 'react';
import { MantineProvider, Container, Title, TextInput, Button, Group } from '@mantine/core';

function AddScreening() {
  const [form, setForm] = useState({ movieId: '', dateTime: '', room: '' });
  const [message, setMessage] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5214/api/Screening/add-screening', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          movieId: parseInt(form.movieId),
          dateTime: form.dateTime,
          room: form.room
        }),
      });
      if (response.ok) {
        setMessage('Vetítés hozzáadva!');
        setForm({ movieId: '', dateTime: '', room: '' });
      }
    } catch (error) {
      setMessage('Hiba történt');
    }
  };

  return (
    <MantineProvider>
      <Container>
        <Title order={2}>Új vetítés</Title>
        <form onSubmit={handleSubmit}>
          <TextInput
            label="Film ID"
            value={form.movieId}
            onChange={(e) => setForm({ ...form, movieId: e.target.value })}
            required
          />
          <TextInput
            label="Dátum és idő"
            type="datetime-local"
            value={form.dateTime}
            onChange={(e) => setForm({ ...form, dateTime: e.target.value })}
            required
          />
          <TextInput
            label="Terem"
            value={form.room}
            onChange={(e) => setForm({ ...form, room: e.target.value })}
            required
          />
          <Group mt="md">
            <Button type="submit">Hozzáadás</Button>
          </Group>
        </form>
        
      </Container>
    </MantineProvider>
  );
}

export default AddScreening;
